  column: number;
  endColumn: number;
}

export interface OpCode {
  opcode: string;
  stack?: string[];
  pos?: Pos;
  debugTag?: DebugModeTag;
}

export interface AutoTypedVar {
  name: string;
  pos: Pos;
  type: string;
}

export interface ABI {
  contract: string, abi: Array<ABIEntity>
}

export enum ABIEntityType {
  FUNCTION = 'function',
  CONSTRUCTOR = 'constructor'
}
export type ParamEntity = {
  name: string;
  type: string;
}
export interface ABIEntity {
  type: ABIEntityType;
  name?: string;
  params: Array<ParamEntity>;
  index?: number;
}

export interface StructEntity {
  name: string;
  params: Array<ParamEntity>;
}

export interface AliasEntity {
  name: string;
  type: string;
}

export function compile(
  source: {
    path: string,
    content?: string,
  },
  settings: {
    ast?: boolean,
    asm?: boolean,
    debug?: boolean,
    desc?: boolean,
    outputDir?: string,
    outputToFiles?: boolean,
    cwd?: string,
    cmdPrefix?: string,
    cmdArgs?: string,
    buildType?: string,
    timeout?: number  // in ms
  } = {
    asm: true,
    debug: true,
  }
): CompileResult {
  const st = Date.now();
  const sourcePath = source.path;
  const srcDir = dirname(sourcePath);
  const curWorkingDir = settings.cwd || srcDir;
  const sourceFileName = basename(sourcePath);
  const outputDir = settings.outputDir || srcDir;
  const timeout = settings.timeout || 1200000;
  const outputFiles = {};
  try {
    const sourceContent = source.content !== undefined ? source.content : readFileSync(sourcePath, 'utf8');
    const cmdPrefix = settings.cmdPrefix || getDefaultScryptc();
    const cmd = `${cmdPrefix} compile ${settings.asm || settings.desc ? '--asm' : ''} ${settings.ast || settings.desc ? '--ast' : ''} ${settings.debug == false ? '' : '--debug'} -r -o "${outputDir}" ${settings.cmdArgs ? settings.cmdArgs : ''}`;
    let output = execSync(cmd, { input: sourceContent, cwd: curWorkingDir, timeout }).toString();
    // Because the output of the compiler on the win32 platform uses crlf as a newline here we change \r\n to \n. make SYNTAX_ERR_REGSEMANTIC_ERR_REGIMPORT_ERR_REG work.
    output = output.split(/\r?\n/g).join('\n');
    let result: CompileResult = { errors: [], warnings: [] };
    if (output.startsWith('Error:') || output.startsWith('Warning:')) {
      result = getErrorsAndWarnings(output, srcDir, sourceFileName);

      if (result.errors.length > 0) {
        return result;
      }

    }



    if (settings.ast || settings.desc) {
      const outputFilePath = getOutputFilePath(outputDir, 'ast');
      outputFiles['ast'] = outputFilePath;


      const allAst = addSourceLocation(JSONbigAlways.parse(readFileSync(outputFilePath, 'utf8')), srcDir, sourceFileName);

      const sourceUri = path2uri(sourcePath);
      result.file = sourceUri;
      result.ast = allAst[sourceUri];
      delete allAst[sourceUri];
      result.dependencyAsts = allAst;
      result.alias = getAliasDeclaration(result.ast, allAst);

      const staticConstInt = getStaticConstIntDeclaration(result.ast, allAst);
      const { contract: name, abi } = getABIDeclaration(result.ast, result.alias, staticConstInt);

      result.abi = abi;
      result.contract = name;
      result.structs = getStructDeclaration(result.ast, allAst);

    }

    let asmObj = null;

    if (settings.asm || settings.desc) {
      const outputFilePath = getOutputFilePath(outputDir, 'asm');
      outputFiles['asm'] = outputFilePath;

      asmObj = JSON.parse(readFileSync(outputFilePath, 'utf8'));
      const sources = asmObj.sources;
      result.asm = asmObj.output.map(item => {

        if (!settings.debug) {
          return {
            opcode: item.opcode
          };
        }

        const match = SOURCE_REG.exec(item.src);

        if (match && match.groups) {
          const fileIndex = parseInt(match.groups.fileIndex);

          let debugTag: DebugModeTag | undefined;

          const tagStr = match.groups.tagStr;
          if (/\w+\.\w+:0/.test(tagStr)) {
            debugTag = DebugModeTag.FuncStart;
          }
          if (/\w+\.\w+:1/.test(tagStr)) {
            debugTag = DebugModeTag.FuncEnd;
          }
          if (/loop:0/.test(tagStr)) {
            debugTag = DebugModeTag.LoopStart;
          }

          const pos: Pos | undefined = sources[fileIndex] ? {
            file: sources[fileIndex] ? getFullFilePath(sources[fileIndex], srcDir, sourceFileName) : undefined,
            line: sources[fileIndex] ? parseInt(match.groups.line) : undefined,
            endLine: sources[fileIndex] ? parseInt(match.groups.endLine) : undefined,
            column: sources[fileIndex] ? parseInt(match.groups.col) : undefined,
            endColumn: sources[fileIndex] ? parseInt(match.groups.endCol) : undefined,
          } : undefined;

          return {
            opcode: item.opcode,
            stack: item.stack,
            pos: pos,
            debugTag
          };
        }
        throw new Error('Compile Failed: Asm output parsing Error!');
      });


      if (settings.debug) {
        result.autoTypedVars = asmObj.autoTypedVars.map(item => {
          const match = SOURCE_REG.exec(item.src);
          if (match && match.groups) {
            const fileIndex = parseInt(match.groups.fileIndex);

            const pos: Pos | undefined = sources[fileIndex] ? {
              file: sources[fileIndex] ? getFullFilePath(sources[fileIndex], srcDir, sourceFileName) : undefined,
              line: sources[fileIndex] ? parseInt(match.groups.line) : undefined,
              endLine: sources[fileIndex] ? parseInt(match.groups.endLine) : undefined,
              column: sources[fileIndex] ? parseInt(match.groups.col) : undefined,
              endColumn: sources[fileIndex] ? parseInt(match.groups.endCol) : undefined,
            } : undefined;
            return {
              name: item.name,
              type: item.type,
              pos: pos
            };
          }
        });
      }
    }

    if (settings.desc) {
      settings.outputToFiles = true;
      const outputFilePath = getOutputFilePath(outputDir, 'desc');
      outputFiles['desc'] = outputFilePath;
      const description: ContractDescription = {
        version: CURRENT_CONTRACT_DESCRIPTION_VERSION,
        compilerVersion: compilerVersion(settings.cmdPrefix ? settings.cmdPrefix : getDefaultScryptc()),
        contract: result.contract,
        md5: md5(sourceContent),
        structs: result.structs || [],
        alias: result.alias || [],
        abi: result.abi || [],
        buildType: settings.buildType || BuildType.Debug,
        file: '',
        asm: result.asm.map(item => item['opcode'].trim()).join(' '),
        sources: [],
        sourceMap: []
      };

      if (settings.debug && asmObj) {
        Object.assign(description, {
          file: result.file,
          sources: asmObj.sources.map(source => getFullFilePath(source, srcDir, sourceFileName)),
          sourceMap: asmObj.output.map(item => item.src),
        });
      }
      writeFileSync(outputFilePath, JSON.stringify(description, null, 4));

      result.compilerVersion = description.compilerVersion;
      result.md5 = description.md5;
    }

    return result;
  } finally {
    if (settings.outputToFiles) {
      Object.keys(outputFiles).forEach(outputType => {
        const file = outputFiles[outputType];
        if (existsSync(file)) {
          if (settings[outputType]) {
            // rename all output files
            renameSync(file, file.replace('stdin', basename(sourcePath, '.scrypt')));
          } else {
            unlinkSync(file);
          }
        }
      });
    } else {
      // cleanup all output files
      Object.values<string>(outputFiles).forEach(file => {
        if (existsSync(file)) {
          unlinkSync(file);
        }
      });
    }
    // console.log('compile time spent: ', Date.now() - st)
  }
}

export function compilerVersion(cwd?: string): string {
  const text = execSync(`${cwd} version`).toString();
  return /Version:\s*([^\s]+)\s*/.exec(text)[1];
}

function addSourceLocation(astRoot, basePath: string, curFileName: string) {
  for (const fileName in astRoot) {
    if (fileName === 'std') {
      astRoot['std'] = _addSourceLocationProperty(astRoot['std'], 'std');
    } else {
      const realFileName = fileName === 'stdin' ? curFileName : fileName;
      const uri = path2uri(join(basePath, realFileName));
      astRoot[uri] = _addSourceLocationProperty(astRoot[fileName], uri);
      delete astRoot[fileName];
    }
  }
  return astRoot;
}

function _addSourceLocationProperty(astObj, uri: string | null) {

  if (!(typeof astObj === 'object')) { return astObj; }
  for (const field in astObj) {
    const value = astObj[field];
    if (field === 'src') {
      const matches = /:(\d+):(\d+):(\d+):(\d+)/.exec(value);
      if (!matches) {
        astObj.loc = null;
      } else {
        astObj.loc = {
          source: uri,
          start: { line: parseInt(matches[1]), column: parseInt(matches[2]) },
          end: { line: parseInt(matches[3]), column: parseInt(matches[4]) }
        };
      }
      delete astObj['src'];
    } else if (typeof value === 'object') {
      _addSourceLocationProperty(value, uri);
    }
  }

  return astObj;
}

function getOutputFilePath(baseDir: string, target: 'ast' | 'asm' | 'desc'): string {
  return join(baseDir, `stdin_${target}.json`);
}

function getFullFilePath(relativePath: string, baseDir: string, curFileName: string): string {
  if (relativePath.endsWith('stdin')) {
    return join(baseDir, curFileName); // replace 'stdin' with real current compiling file name.
  }

  if (relativePath === 'std') {
    return 'std'; // 
  }

  return join(baseDir, relativePath);
}

function getConstructorDeclaration(mainContract): ABIEntity {
  // explict constructor
  if (mainContract['constructor']) {
    return {
      type: ABIEntityType.CONSTRUCTOR,
      params: mainContract['constructor']['params'].map(p => { return { name: p['name'], type: p['type'] }; }),
    };
  } else {
    // implicit constructor
    if (mainContract['properties']) {
      return {
        type: ABIEntityType.CONSTRUCTOR,
        params: mainContract['properties'].map(p => { return { name: p['name'].replace('this.', ''), type: p['type'] }; }),
      };
    }
  }
}

function getPublicFunctionDeclaration(mainContract): ABIEntity[] {
  let pubIndex = 0;
  const interfaces: ABIEntity[] =
    mainContract['functions']
      .filter(f => f['visibility'] === 'Public')
      .map(f => {
        const entity: ABIEntity = {
          type: ABIEntityType.FUNCTION,
          name: f['name'],
          index: f['nodeType'] === 'Constructor' ? undefined : pubIndex++,
          params: f['params'].map(p => { return { name: p['name'], type: p['type'] }; }),
        };
        return entity;
      });
  return interfaces;
}



export function getABIDeclaration(astRoot, alias: AliasEntity[], staticConstInt: Record<string, number>): ABI {
  const mainContract = astRoot['contracts'][astRoot['contracts'].length - 1];
  if (!mainContract) {
    return {
      contract: '',
      abi: []
    };
  }

  const interfaces: ABIEntity[] = getPublicFunctionDeclaration(mainContract);
  const constructorABI = getConstructorDeclaration(mainContract);

  interfaces.push(constructorABI);

  interfaces.forEach(abi => {
    abi.params = abi.params.map(param => {
      return Object.assign(param, {
        type: resolveAbiParamType(mainContract['name'], param.type, alias, staticConstInt)
      });
    });
  });

  return {
    contract: mainContract['name'],
    abi: interfaces
  };
}


function resolveAbiParamType(contract: string, type: string, alias: AliasEntity[], staticConstInt: Record<string, number>): string {
  const resolvedConstIntType = resolveArrayTypeWithConstInt(contract, type, staticConstInt);
  const resolvedAliasType = resolveType(alias, resolvedConstIntType);

  if (isStructType(resolvedAliasType)) {
    return getStructNameByType(resolvedAliasType);
  } else if (isArrayType(resolvedAliasType)) {
    const [elemTypeName, arraySizes] = arrayTypeAndSize(resolvedAliasType);
    const elemType = isStructType(elemTypeName) ? getStructNameByType(elemTypeName) : elemTypeName;
    return toLiteralArrayType(elemType, arraySizes);
  }

  return resolvedAliasType;
}

export function resolveArrayTypeWithConstInt(contract: string, type: string, staticConstInt: Record<string, number>): string {

  if (isArrayType(type)) {
    const [elemTypeName, arraySizes] = arrayTypeAndSizeStr(type);

    const sizes = arraySizes.map(size => {
      if (/^(\d)+$/.test(size)) {
        return parseInt(size);
      } else {
        if (size.indexOf('.') > 0) {
          return staticConstInt[size];
        } else {
          return staticConstInt[`${contract}.${size}`];
        }
      }
    });

    return toLiteralArrayType(elemTypeName, sizes);
  }
  return type;
}


export function getStructDeclaration(astRoot, dependencyAsts): Array<StructEntity> {


  const allAst = [astRoot];

  Object.keys(dependencyAsts).forEach(key => {
    allAst.push(dependencyAsts[key]);
  });

  return allAst.map(ast => {
    return oc(ast).structs([]).map(s => ({
      name: s['name'],
      params: s['fields'].map(p => { return { name: p['name'], type: p['type'] }; }),
    }));
  }).flat(1);
}


export function getAliasDeclaration(astRoot, dependencyAsts): Array<AliasEntity> {

  const allAst = [astRoot];

  Object.keys(dependencyAsts).forEach(key => {
    allAst.push(dependencyAsts[key]);
  });

  return allAst.map(ast => {
    return oc(ast).alias([]).map(s => ({
      name: s['alias'],
      type: s['type'],
    }));
  }).flat(1);
}

export function getStaticConstIntDeclaration(astRoot, dependencyAsts): Record<string, number> {

  const allAst = [astRoot];
  Object.keys(dependencyAsts).forEach(key => {
    allAst.push(dependencyAsts[key]);
  });

  return allAst.map((ast, index) => {
    return oc(ast).contracts([]).map(contract => {
      return oc(contract).statics([]).filter(s => (
        s.const === true && s.expr.nodeType === 'IntLiteral'
      )).map(s => {

        return {
          name: `${contract.name}.${s.name}`,
          value: s.expr.value
        };
      });
    });
  }).flat(Infinity).reduce((acc, item) => (acc[item.name] = item.value, acc), {} as Record<string, number>);
}


export function getPlatformScryptc(): string {
  switch (os.platform()) {
  case 'win32':
    return 'compiler/scryptc/win32/scryptc.exe';
  case 'linux':
    return 'compiler/scryptc/linux/scryptc';
  case 'darwin':
    return 'compiler/scryptc/mac/scryptc';
  default:
    throw `sCrypt doesn't support ${os.platform()}`;
  }
}

function vscodeExtensionPath(): string {
  const homedir = os.homedir();
  const extensionPath = join(homedir, '.vscode/extensions');
  if (!existsSync(extensionPath)) {
    throw 'No Visual Studio Code extensions found. Please ensure Visual Studio Code is installed.';
  }
  return extensionPath;
}

function findVscodeScrypt(extensionPath: string): string {
  const sCryptPrefix = 'bsv-scrypt.scrypt-';
  let versions = readdirSync(extensionPath).reduce((filtered, item) => {
    if (item.indexOf(sCryptPrefix) > -1) {
      const version = item.substring(sCryptPrefix.length);
      if (compareVersions.validate(version)) {
        filtered.push(version);
      }
    }
    return filtered;
  }, []);

  // compareVersions is ascending, so reverse.
  versions = versions.sort(compareVersions).reverse();
  return sCryptPrefix + versions[0];
}

export function getDefaultScryptc(): string {


  const extensionPath = vscodeExtensionPath();

  const sCrypt = findVscodeScrypt(extensionPath);
  if (!sCrypt) {
    throw `No sCrypt extension found. Please install it at extension marketplace:
		https://marketplace.visualstudio.com/items?itemName=bsv-scrypt.sCrypt`;
  }

  const scryptc = join(extensionPath, sCrypt, getPlatformScryptc());

  if (!existsSync(scryptc)) {
    throw 'No sCrypt compiler found. Please update your sCrypt extension to the latest version';
  }

  return scryptc;
}



export function desc2CompileResult(description: ContractDescription): CompileResult {
  const sources = description.sources;
  const asm = description.asm.split(' ');
  const errorMessage = 'Contract description version deprecated,  Please update your sCrypt extension to the latest version and recompile';
  if (description.version === undefined) {
    throw new Error(errorMessage);
  }

  if (description.version < CURRENT_CONTRACT_DESCRIPTION_VERSION) {
    console.warn(errorMessage);
  }

  const result: CompileResult = {
    compilerVersion: description.compilerVersion,
    contract: description.contract,
    md5: description.md5,
    abi: description.abi,
    structs: description.structs,
    alias: description.alias,
    file: description.file,
    buildType: description.buildType || BuildType.Debug,
    errors: [],
    warnings: [],
    asm: asm.map((opcode, index) => {
      const item = description.sourceMap && description.sourceMap[index];
      if (item) {
        const match = SOURCE_REG.exec(item);
        if (match && match.groups) {
          const fileIndex = parseInt(match.groups.fileIndex);

          const pos: Pos | undefined = sources[fileIndex] ? {
            file: sources[fileIndex],
            line: sources[fileIndex] ? parseInt(match.groups.line) : undefined,
            endLine: sources[fileIndex] ? parseInt(match.groups.endLine) : undefined,
            column: sources[fileIndex] ? parseInt(match.groups.col) : undefined,
            endColumn: sources[fileIndex] ? parseInt(match.groups.endCol) : undefined,
          } : undefined;

          return {
            pos: pos,
            opcode: opcode,
            stack: []
          };
        }
      }

      return {
        opcode: opcode,
        stack: []
      };
    })
  };
  return result;
}

function getErrorsAndWarnings(output: string, srcDir: string, sourceFileName: string): CompileResult {
  const warnings: Warning[] = [...output.matchAll(WARNING_REG)].map(match => {
    const filePath = oc(match.groups).filePath('');
    let message = oc(match.groups).message('');

    message = message.replace(/Variable `(?<varName>\w+)` shadows existing binding at (?<fileIndex>[^\s]+):(?<line>\d+):(?<column>\d+):(?<line1>\d+):(?<column1>\d+)/,
      'Variable `$1` shadows existing binding at $3:$4:$5:$6');
    return {
      type: CompileErrorType.Warning,
      filePath: getFullFilePath(filePath, srcDir, sourceFileName),
      position: [{
        line: parseInt(oc(match.groups).line('-1')),
        column: parseInt(oc(match.groups).column('-1')),
      }, {
        line: parseInt(oc(match.groups).line1('-1')),
        column: parseInt(oc(match.groups).column1('-1')),
      }],
      message: message
    };
  });


  if (output.match(INTERNAL_ERR_REG)) {
    return {
      warnings: warnings,
      errors: [{
        type: CompileErrorType.InternalError,
        filePath: getFullFilePath('stdin', srcDir, sourceFileName),
        message: `Compiler internal error: ${oc(output.match(INTERNAL_ERR_REG).groups).message('')}`,
        position: [{
          line: 1,
          column: 1
        }, {
          line: 1,
          column: 1
        }]
      }]
    };
  } else if (output.includes('Syntax error:')) {
    const syntaxErrors: CompileError[] = [...output.matchAll(SYNTAX_ERR_REG)].map(match => {
      const filePath = oc(match.groups).filePath('');
      const unexpected = oc(match.groups).unexpected('');
      const expecting = oc(match.groups).expecting('');
      return {
        type: CompileErrorType.SyntaxError,
        filePath: getFullFilePath(filePath, srcDir, sourceFileName),
        position: [{
          line: parseInt(oc(match.groups).line('-1')),
          column: parseInt(oc(match.groups).column('-1')),
        }],
        message: oc(match.groups).message(`unexpected ${unexpected}\nexpecting ${expecting}`),
        unexpected,
        expecting,
      };
    });
    return {
      warnings: warnings,
      errors: syntaxErrors
    };
  }
  else {

    const semanticErrors: CompileError[] = [...output.matchAll(SEMANTIC_ERR_REG)].map(match => {
      let message = oc(match.groups).message('');
      const filePath = oc(match.groups).filePath('');

      message = message.replace(/Symbol `(?<varName>\w+)` already defined at (?<fileIndex>[^\s]+):(?<line>\d+):(?<column>\d+):(?<line1>\d+):(?<column1>\d+)/,
        'Symbol `$1` already defined at $3:$4:$5:$6');

      return {
        type: CompileErrorType.SemanticError,
        filePath: getFullFilePath(filePath, srcDir, sourceFileName),
        position: [{
          line: parseInt(oc(match.groups).line('-1')),
          column: parseInt(oc(match.groups).column('-1')),
        }, {
          line: parseInt(oc(match.groups).line1('-1')),
          column: parseInt(oc(match.groups).column1('-1')),
        }],
        message: message
      };
    });
    return {
      warnings: warnings,
      errors: semanticErrors
    };
  }
}

import { oc } from 'ts-optchain';
import { int2Asm, bsv, arrayTypeAndSize, genLaunchConfigFile, getStructNameByType, isArrayType, isStructType, checkArray, flatternArray, typeOfArg, subscript, flatternStruct, resolveType, int2Value, asm2int, createStruct, createArray, asm2ScryptType } from './utils';
import { AbstractContract, TxContext, VerifyResult, AsmVarValues, buildTypeResolver } from './contract';
import { ScryptType, Bool, Int, SingletonParamType, SupportedParamType, Struct, Bytes } from './scryptTypes';
import { ABIEntityType, ABIEntity, ParamEntity, AliasEntity } from './compilerWrapper';
const BN = bsv.crypto.BN;

export interface Script {
  toASM(): string;
  toHex(): string;
}

export type FileUri = string;

/**
     * Configuration for a debug session.
     */
export interface DebugConfiguration {
  type: 'scrypt';
  request: 'launch';
  internalConsoleOptions: 'openOnSessionStart',
  name: string;
  program: string;
  constructorArgs: SupportedParamType[];
  pubFunc: string;
  pubFuncArgs: SupportedParamType[];
  asmArgs?: AsmVarValues;
  txContext?: any;
}

export interface DebugLaunch {
  version: '0.2.0';
  configurations: DebugConfiguration[];
}

function escapeRegExp(stringToGoIntoTheRegex) {
  return stringToGoIntoTheRegex.replace(/[-/\\^$*+?.()|[\]{}]/g, '\\$&');
}

export interface Argument {
  name: string,
  type: string,
  value: SupportedParamType
}

export type Arguments = Argument[];


export class FunctionCall {

  readonly contract: AbstractContract;

  readonly args: Arguments = [];

  private _unlockingScriptAsm?: string;

  private _lockingScriptAsm?: string;

  get unlockingScript(): Script | undefined {
    return this._unlockingScriptAsm === undefined ? undefined : bsv.Script.fromASM(this._unlockingScriptAsm);
  }

  get lockingScript(): Script | undefined {
    return this._lockingScriptAsm === undefined ? undefined : bsv.Script.fromASM(this._lockingScriptAsm);
  }

  init(asmVarValues: AsmVarValues): void {
    for (const key in asmVarValues) {
      const val = asmVarValues[key];
      const re = new RegExp(`\\$${key}`, 'g');
      this._lockingScriptAsm = this._lockingScriptAsm.replace(re, val);
    }
  }

  constructor(
    public methodName: string,
    public params: SupportedParamType[],
    binding: {
      contract: AbstractContract;
      lockingScriptASM?: string;
      unlockingScriptASM?: string;
    }
  ) {

    if (binding.lockingScriptASM === undefined && binding.unlockingScriptASM === undefined) {
      throw new Error('param binding.lockingScriptASM & binding.unlockingScriptASM cannot both be empty');
    }

    this.contract = binding.contract;


    this.args = Object.getPrototypeOf(this.contract).constructor.abi.filter((entity: ABIEntity) => {
      if ('constructor' === methodName) {
        return entity.type === 'constructor';
      }
      return entity.name === methodName;
    }).map((entity: ABIEntity) => {
      return entity.params.map((param, index) => {
        return {
          name: param.name,
          type: param.type,
          value: params[index]
        };
      });
    }).flat(1);

    if (binding.lockingScriptASM) {
      this._lockingScriptAsm = binding.lockingScriptASM;
    }

    if (binding.unlockingScriptASM) {
      this._unlockingScriptAsm = binding.unlockingScriptASM;
    }
  }

  toASM(): string {
    if (this.lockingScript) {
      return this.lockingScript.toASM();
    } else {
      return this.unlockingScript.toASM();
    }
  }

  toString(): string {
    return this.toHex();
  }

  toScript(): Script {
    return bsv.Script.fromASM(this.toASM());
  }

  toHex(): string {
    return this.toScript().toHex();
  }



  genLaunchConfigFile(txContext?: TxContext): FileUri {

    const constructorArgs: SupportedParamType[] = this.contract.scriptedConstructor.params;

    const pubFuncArgs: SupportedParamType[] = this.params;
    const pubFunc: string = this.methodName;
    const name = `Debug ${Object.getPrototypeOf(this.contract).constructor.contractName}`;
    const program = `${Object.getPrototypeOf(this.contract).constructor.file}`;

    const asmArgs: AsmVarValues = this.contract.asmArgs || {};
    const dataPart: string = this.contract.dataPart ? this.contract.dataPart.toASM() : undefined;
    const txCtx: TxContext = Object.assign({}, this.contract.txContext || {}, txContext || {}, { opReturn: dataPart });

    return genLaunchConfigFile(constructorArgs, pubFuncArgs, pubFunc, name, program, txCtx, asmArgs);
  }

  verify(txContext?: TxContext): VerifyResult {
    if (this.unlockingScript) {
      const result = this.contract.run_verify(this.unlockingScript.toASM(), txContext, this.args);

      if (!result.success) {
        const debugUrl = this.genLaunchConfigFile(txContext);
        if (debugUrl) {
          result.error = result.error + `\t[Launch Debugger](${debugUrl.replace(/file:/i, 'scryptlaunch:')})\n`;
        }
      }
      return result;
    }

    return {
      success: false,
      error: 'verification failed, missing unlockingScript'
    };
  }

}

export class ABICoder {

  constructor(public abi: ABIEntity[], public alias: AliasEntity[]) { }


  encodeConstructorCall(contract: AbstractContract, asmTemplate: string, ...args: SupportedParamType[]): FunctionCall {

    const constructorABI = this.abi.filter(entity => entity.type === ABIEntityType.CONSTRUCTOR)[0];
    const cParams = oc(constructorABI).params([]);

    if (args.length !== cParams.length) {
      throw new Error(`wrong number of arguments for #constructor, expected ${cParams.length} but got ${args.length}`);
    }

    // handle array type
    const cParams_: Array<ParamEntity> = [];
    const args_: SupportedParamType[] = [];
    cParams.forEach((param, index) => {
      const arg = args[index];
      const finalType = resolveType(this.alias, param.type);
      if (isArrayType(finalType)) {
        const [elemTypeName, arraySizes] = arrayTypeAndSize(finalType);

        if (Array.isArray(arg)) {
          if (checkArray(arg, [elemTypeName, arraySizes])) {
            // flattern array
            flatternArray(arg, param.name, finalType).forEach((e, idx) => {
              cParams_.push({ name: e.name, type: e.type });
              args_.push(e.value);
            });
          } else {
            throw new Error(`constructor ${index}-th parameter should be ${finalType}`);
          }
        } else {
          throw new Error(`constructor ${index}-th parameter should be ${finalType}`);
        }
      } else if (isStructType(finalType)) {

        const argS = arg as Struct;

        if (finalType != argS.finalType) {
          throw new Error(`expect struct ${param.type} but got struct ${argS.type}`);
        }

        flatternStruct(argS, param.name).forEach(v => {
          cParams_.push({ name: `${v.name}`, type: v.type });
          args_.push(v.value);
        });
      }
      else {
        cParams_.push(param);
        args_.push(arg);
      }
    });

    let lsASM = asmTemplate;

    cParams_.forEach((param, index) => {
      if (!asmTemplate.includes(`$${param.name}`)) {
        throw new Error(`abi constructor params mismatch with args provided: missing ${param.name} in ASM tempalte`);
      }

      const re = param.name.endsWith(']') ? new RegExp(`\\B${escapeRegExp(`$${param.name}`)}\\B`, 'g') : new RegExp(`\\B${escapeRegExp(`$${param.name}`)}\\b`, 'g');
      lsASM = lsASM.replace(re, this.encodeParam(args_[index], param));
    });

    return new FunctionCall('constructor', args, { contract, lockingScriptASM: lsASM });
  }

  encodeConstructorCallFromASM(contract: AbstractContract, asmTemplate: string, lsASM: string): FunctionCall {
    const constructorABI = this.abi.filter(entity => entity.type === ABIEntityType.CONSTRUCTOR)[0];
    const cParams = oc(constructorABI).params([]);

    const opcodesMap = new Map<string, string>();

    const asmTemplateOpcodes = asmTemplate.split(' ');
    const asmOpcodes = lsASM.split(' ');

    if (asmTemplateOpcodes.length != asmOpcodes.length) {
      throw new Error(`the raw script cannot match the contract ${AbstractContract.contractName}`);
    }

    asmTemplateOpcodes.forEach((opcode, index) => {

      if (opcode.startsWith('$')) {
        opcodesMap.set(opcode, asmOpcodes[index]);
      }
    });


    const finalTypeResolver = buildTypeResolver(this.alias);

    const args: SupportedParamType[] = [];
    cParams.forEach((param, index) => {
      const finalType = finalTypeResolver(param.type);


      if (isStructType(finalType)) {

        const stclass = contract.getTypeClassByType(param.type);

        args.push(createStruct(contract, stclass as typeof Struct, param.name, opcodesMap, finalTypeResolver));
      } else if (isArrayType(finalType)) {

        args.push(createArray(contract, finalType, param.name, opcodesMap, finalTypeResolver));

      } else {
        args.push(asm2ScryptType(finalType, opcodesMap.get(`$${param.name}`)));
      }

    });


    return new FunctionCall('constructor', args, { contract, lockingScriptASM: lsASM });
  }

  encodePubFunctionCall(contract: AbstractContract, name: string, args: SupportedParamType[]): FunctionCall {

    for (const entity of this.abi) {
      if (entity.name === name) {
        if (entity.params.length !== args.length) {
          throw new Error(`wrong number of arguments for #${name}, expected ${entity.params.length} but got ${args.length}`);
        }
        let asm = this.encodeParams(args, entity.params);
        if (this.abi.length > 2 && entity.index !== undefined) {
          // selector when there are multiple public functions
          const pubFuncIndex = entity.index;
          asm += ` ${int2Asm(pubFuncIndex.toString())}`;
        }
        return new FunctionCall(name, args, { contract, unlockingScriptASM: asm });
      }
    }

    throw new Error(`no function named '${name}' found in abi`);
  }

  encodeParams(args: SupportedParamType[], paramsEntitys: ParamEntity[]): string {
    return args.map((arg, i) => this.encodeParam(arg, paramsEntitys[i])).join(' ');
  }

  encodeParamArray(args: SingletonParamType[], arrayParm: ParamEntity): string {
    if (args.length === 0) {
      throw new Error('Empty array not allowed');
    }

    const t = typeof args[0];

    if (!args.every(arg => typeof arg === t)) {
      throw new Error('Array arguments are not of the same type');
    }
    const finalType = resolveType(this.alias, arrayParm.type);

    const [elemTypeName, arraySizes] = arrayTypeAndSize(finalType);
    if (checkArray(args, [elemTypeName, arraySizes])) {
      return flatternArray(args, arrayParm.name, finalType).map(arg => {
        return this.encodeParam(arg.value, { name: arg.name, type: arg.type });
      }).join(' ');
    } else {
      throw new Error(`checkArray ${arrayParm.type} fail`);
    }
  }


  encodeParam(arg: SupportedParamType, paramEntity: ParamEntity): string {

    const finalType = resolveType(this.alias, paramEntity.type);
    if (isArrayType(finalType)) {
      if (Array.isArray(arg)) {
        return this.encodeParamArray(arg, paramEntity);
      } else {
        const scryptType = typeOfArg(arg);
        throw new Error(`expect param ${paramEntity.name} as ${finalType} but got ${scryptType}`);
      }
    }

    if (isStructType(finalType)) {

      if (Struct.isStruct(arg)) {
        const argS = arg as Struct;
        if (finalType != argS.finalType) {
          throw new Error(`expect struct ${paramEntity.type} but got struct ${argS.type}`);
        }
      } else {
        const scryptType = (arg as ScryptType).type;
        throw new Error(`expect param ${paramEntity.name} as struct ${getStructNameByType(paramEntity.type)} but got ${scryptType}`);
      }
    }


    const scryptType = typeOfArg(arg);
    if (scryptType != finalType) {
      throw new Error(`wrong argument type, expected ${finalType} or ${paramEntity.type} but got ${scryptType}`);
    }

    const typeofArg = typeof arg;

    if (typeofArg === 'boolean') {
      arg = new Bool(arg as boolean);
    }

    if (typeofArg === 'number') {
      arg = new Int(arg as number);
    }

    if (typeofArg === 'bigint') {
      arg = new Int(arg as bigint);
    }

    if (typeof arg === 'string') {
      arg = new Int(arg as string);
    }

    return (arg as ScryptType).toASM();
  }

}

import { ABICoder, Arguments, FunctionCall, Script } from './abi';
import { serializeState, State } from './serializer';
import { bsv, DEFAULT_FLAGS, resolveType, path2uri, isStructType, getStructNameByType, isArrayType, arrayTypeAndSize, stripAnsi } from './utils';
import { Struct, SupportedParamType, StructObject, ScryptType, VariableType, Int, Bytes, BasicScryptType, ValueType, TypeResolver, SigHashPreimage, SigHashType } from './scryptTypes';
import { StructEntity, ABIEntity, OpCode, CompileResult, desc2CompileResult, AliasEntity, Pos } from './compilerWrapper';

export interface TxContext {
  tx?: any;
  inputIndex?: number;
  inputSatoshis?: number;
  opReturn?: string;
}


export type VerifyError = string;


export interface VerifyResult {
  success: boolean;
  error?: VerifyError;
}

export interface ContractDescription {
  version: number;
  compilerVersion: string;
  buildType: string;
  contract: string;
  md5: string;
  structs: Array<StructEntity>;
  alias: Array<AliasEntity>
  abi: Array<ABIEntity>;
  asm: string;
  file: string;
  sources: Array<string>;
  sourceMap: Array<string>;
}

export type AsmVarValues = { [key: string]: string }
export type StepIndex = number;

export class AbstractContract {

  public static contractName: string;
  public static abi: ABIEntity[];
  public static asm: string;
  public static abiCoder: ABICoder;
  public static opcodes?: OpCode[];
  public static file: string;
  public static structs: StructEntity[];
  public static types: Record<string, typeof ScryptType>;
  public static asmContract: boolean;


  scriptedConstructor: FunctionCall;
  calls: Map<string, FunctionCall> = new Map();
  asmArgs: AsmVarValues | null = null;

  get lockingScript(): Script {
    let lsASM = this.scriptedConstructor.toASM();
    if (typeof this._dataPart === 'string') {
      const dp = this._dataPart.trim();
      if (dp) {
        lsASM += ` OP_RETURN ${dp}`;
      } else {
        lsASM += ' OP_RETURN';  // note there is no space after op_return
      }
    }
    return bsv.Script.fromASM(lsASM.trim());
  }

  private _txContext?: TxContext;

  set txContext(txContext: TxContext) {
    this._txContext = txContext;
  }

  get txContext(): TxContext {
    return this._txContext;
  }

  // replace assembly variables with assembly values
  replaceAsmVars(asmVarValues: AsmVarValues): void {
    this.asmArgs = asmVarValues;
    this.scriptedConstructor.init(asmVarValues);
  }

  static findSrcInfo(interpretStates: any[], opcodes: OpCode[], stepIndex: number, opcodesIndex: number): OpCode | undefined {
    while (--stepIndex > 0 && --opcodesIndex > 0) {
      if (opcodes[opcodesIndex].pos && opcodes[opcodesIndex].pos.file !== 'std' && opcodes[opcodesIndex].pos.line > 0 && interpretStates[stepIndex].step.fExec) {
        return opcodes[opcodesIndex];
      }
    }
  }


  getTypeClassByType(type: string): typeof ScryptType {
    const types: typeof ScryptType[] = Object.getPrototypeOf(this).constructor.types;

    if (isStructType(type)) {
      const structName = getStructNameByType(type);
      if (Object.prototype.hasOwnProperty.call(types, structName)) {
        return types[structName];
      }
    } else {
      return types[type];
    }
  }

  run_verify(unlockingScriptASM: string, txContext?: TxContext, args?: Arguments): VerifyResult {
    const txCtx: TxContext = Object.assign({}, this._txContext || {}, txContext || {});

    const us = bsv.Script.fromASM(unlockingScriptASM.trim());
    const ls = this.lockingScript;
    const tx = txCtx.tx;
    const inputIndex = txCtx.inputIndex || 0;
    const inputSatoshis = txCtx.inputSatoshis || 0;


    bsv.Script.Interpreter.MAX_SCRIPT_ELEMENT_SIZE = Number.MAX_SAFE_INTEGER;
    bsv.Script.Interpreter.MAXIMUM_ELEMENT_SIZE = Number.MAX_SAFE_INTEGER;


    const bsi = bsv.Script.Interpreter();

    let stepCounter: StepIndex = 0;
    const interpretStates: { step: any, mainstack: any, altstack: any }[] = [];
    bsi.stepListener = function (step: any, stack: any[], altstack: any[]) {
      interpretStates.push({ mainstack: stack, altstack: altstack, step: step });
      stepCounter++;
    };


    const opcodes: OpCode[] = Object.getPrototypeOf(this).constructor.opcodes;

    const result = bsi.verify(us, ls, tx, inputIndex, DEFAULT_FLAGS, new bsv.crypto.BN(inputSatoshis));

    let error = result ? '' : `VerifyError: ${bsi.errstr}`;



    // some time there is no opcodes, such as when sourcemap flag is closeed. 
    if (opcodes) {
      const offset = unlockingScriptASM.trim().split(' ').length;
      // the complete script may have op_return and data, but compiled output does not have it. So we need to make sure the index is in boundary.

      const lastStepIndex = stepCounter - 1;

      if (typeof this._dataPart === 'string') {
        opcodes.push({ opcode: 'OP_RETURN', stack: [] });
        const dp = this._dataPart.trim();
        if (dp) {
          dp.split(' ').forEach(data => {
            opcodes.push({ opcode: data, stack: [] });
          });
        }
      }

      const opcodeIndex = lastStepIndex - offset;


      if (!result && opcodes[opcodeIndex]) {

        const opcode = opcodes[opcodeIndex];

        if (!opcode.pos || opcode.pos.file === 'std') {

          const srcInfo = AbstractContract.findSrcInfo(interpretStates, opcodes, lastStepIndex, opcodeIndex);

          if (srcInfo) {
            opcode.pos = srcInfo.pos;
          }
        }

        // in vscode termianal need to use [:] to jump to file line, but here need to use [#] to jump to file line in output channel.
        if (opcode.pos) {
          error = `VerifyError: ${bsi.errstr} \n\t[Go to Source](${path2uri(opcode.pos.file)}#${opcode.pos.line})  fails at ${opcode.opcode}\n`;

          if (args && ['OP_CHECKSIG', 'OP_CHECKSIGVERIFY', 'OP_CHECKMULTISIG', 'OP_CHECKMULTISIGVERIFY'].includes(opcode.opcode)) {
            if (!txCtx) {
              throw new Error('should provide txContext when verify');
            } if (!tx) {
              throw new Error('should provide txContext.tx when verify');
            }
          }
        }
      }
    }

    return {
      success: result,
      error: error
    };
  }

  private _dataPart: string | undefined;

  set dataPart(dataInScript: Script | undefined) {
    throw new Error('Setter for dataPart is not available. Please use: setDataPart() instead');
  }

  get dataPart(): Script | undefined {
    return this._dataPart !== undefined ? bsv.Script.fromASM(this._dataPart) : undefined;
  }

  setDataPart(state: State | string): void {
    if (typeof state === 'string') {
      // TODO: validate hex string
      this._dataPart = state.trim();
    } else {
      this._dataPart = serializeState(state);
    }
  }

  get codePart(): Script {
    const codeASM = this.scriptedConstructor.toASM();
    // note: do not trim the trailing space
    return bsv.Script.fromASM(codeASM + ' OP_RETURN');
  }

  static getAsmVars(contractAsm, instAsm): AsmVarValues | null {
    const regex = /(\$\S+)/g;
    const vars = contractAsm.match(regex);
    if (vars === null) {
      return null;
    }
    const asmArray = contractAsm.split(/\s/g);
    const lsASMArray = instAsm.split(/\s/g);
    const result = {};
    for (let i = 0; i < asmArray.length; i++) {
      for (let j = 0; j < vars.length; j++) {
        if (vars[j] === asmArray[i]) {
          result[vars[j].replace('$', '')] = lsASMArray[i];
        }
      }
    }
    return result;
  }

  public arguments(pubFuncName: string): Arguments {
    if (pubFuncName === 'constructor') {
      return this.scriptedConstructor.args;
    }

    if (this.calls.has(pubFuncName)) {
      return this.calls.get(pubFuncName).args;
    }

    return [];
  }
}


const invalidMethodName = ['arguments', 'setDataPart', 'run_verify', 'replaceAsmVars', 'asmVars', 'asmArguments', 'dataPart', 'lockingScript', 'txContext'];

export function buildContractClass(desc: CompileResult | ContractDescription): any {

  if (!desc.contract) {
    throw new Error('missing field `contract` in description');
  }

  if (!desc.abi) {
    throw new Error('missing field `abi` in description');
  }

  if (!desc.asm) {
    throw new Error('missing field `asm` in description');
  }

  if (!desc['errors']) {
    desc = desc2CompileResult(desc as ContractDescription);
  } else {
    desc = desc as CompileResult;
  }



  const ContractClass = class Contract extends AbstractContract {
    constructor(...ctorParams: SupportedParamType[]) {
      super();
      if (!Contract.asmContract) {
        this.scriptedConstructor = Contract.abiCoder.encodeConstructorCall(this, Contract.asm, ...ctorParams);
      }
    }

    //When create a contract instance using UTXO, 
    //use fromHex or fromASM because you do not know the parameters of constructor.

    /**
     * Create a contract instance using UTXO asm
     * @param hex 
     */
    static fromASM(asm: string) {
      Contract.asmContract = true;
      const obj = new this();
      Contract.asmContract = false;
      obj.scriptedConstructor = Contract.abiCoder.encodeConstructorCallFromASM(obj, Contract.asm, asm);
      return obj;
    }

    /**
     * Create a contract instance using UTXO hex
     * @param hex 
     */
    static fromHex(hex: string) {
      return ContractClass.fromASM((new bsv.Script(hex)).toASM());
    }

    /**
     * Get the parameter of the constructor and inline asm vars,
     * all values is hex string, need convert it to number or bytes on using
     */
    get asmVars(): AsmVarValues | null {
      return AbstractContract.getAsmVars(Contract.asm, this.scriptedConstructor.toASM());
    }

    get asmArguments(): AsmVarValues | null {
      //TODO: @deprecate AbstractContract.getAsmVars , using asmArguments

      return null;
    }

  };

  ContractClass.contractName = desc.contract;
  ContractClass.abi = desc.abi;
  ContractClass.asm = desc.asm.map(item => item['opcode'].trim()).join(' ');
  ContractClass.abiCoder = new ABICoder(desc.abi, desc.alias || []);
  ContractClass.opcodes = desc.asm;
  ContractClass.file = desc.file;
  ContractClass.structs = desc.structs;
  ContractClass.types = buildTypeClasses(desc);


  ContractClass.abi.forEach(entity => {
    if (invalidMethodName.indexOf(entity.name) > -1) {
      throw new Error(`Method name [${entity.name}] is used by scryptlib now, Pelease change you contract method name!`);
    }
    ContractClass.prototype[entity.name] = function (...args: SupportedParamType[]): FunctionCall {
      const call = ContractClass.abiCoder.encodePubFunctionCall(this, entity.name, args);
      this.calls.set(entity.name, call);
      return call;
    };
  });

  return ContractClass;
}



/**
 * @deprecated use buildTypeClasses
 * @param desc CompileResult or ContractDescription
 */
export function buildStructsClass(desc: CompileResult | ContractDescription): Record<string, typeof Struct> {

  const structTypes: Record<string, typeof Struct> = {};

  const structs: StructEntity[] = desc.structs || [];
  const alias: AliasEntity[] = desc.alias || [];
  const finalTypeResolver = buildTypeResolver(alias);
  structs.forEach(element => {
    const name = element.name;

    Object.assign(structTypes, {
      [name]: class extends Struct {
        constructor(o: StructObject) {
          super(o);
          this._typeResolver = finalTypeResolver; //we should assign this before bind
          this.bind();
        }
      }
    });

    structTypes[name].structAst = element;
  });

  return structTypes;
}



export function buildTypeClasses(desc: CompileResult | ContractDescription): Record<string, typeof ScryptType> {

  const structClasses = buildStructsClass(desc);
  const aliasTypes: Record<string, typeof ScryptType> = {};
  const alias: AliasEntity[] = desc.alias || [];
  const finalTypeResolver = buildTypeResolver(alias);
  alias.forEach(element => {
    const finalType = finalTypeResolver(element.name);
    if (isStructType(finalType)) {
      const type = getStructNameByType(finalType);
      Object.assign(aliasTypes, {
        [element.name]: class extends structClasses[type] {
          constructor(o: StructObject) {
            super(o);
            this._type = element.name;
            this._typeResolver = finalTypeResolver;
          }
        }
      });
    } else if (isArrayType(finalType)) {
      //TODO: just return some class, but they are useless
      const [elemTypeName, _] = arrayTypeAndSize(finalType);

      const C = BasicScryptType[elemTypeName];
      if (C) {
        Object.assign(aliasTypes, {
          [element.name]: class extends Array<typeof C> { }
        });
      } else if (isStructType(elemTypeName)) {
        const type = getStructNameByType(elemTypeName);
        const C = structClasses[type];
        Object.assign(aliasTypes, {
          [element.name]: class extends Array<typeof C> { }
        });
      }

    } else {
      const C = BasicScryptType[finalType];
      if (C) {
        const Class = C as typeof ScryptType;
        const aliasClass = class extends Class {
          constructor(o: ValueType) {
            super(o);
            this._type = element.name;
            this._typeResolver = finalTypeResolver;
          }
        };

        Object.assign(aliasTypes, {
          [element.name]: aliasClass
        });
      } else {
        throw new Error(`can not resolve type alias ${element.name} ${element.type}`);
      }
    }
  });

  Object.assign(aliasTypes, structClasses);

  return aliasTypes;
}



export function buildTypeResolver(alias: AliasEntity[]): TypeResolver {
  const resolvedTypes: Record<string, string> = {};
  alias.forEach(element => {
    const finalType = resolveType(alias, element.name);
    resolvedTypes[element.name] = finalType;
  });
  return (alias: string) => {

    if (isStructType(alias)) {
      alias = getStructNameByType(alias);
    }

    let arrayType = '';
    if (isArrayType(alias)) {
      const [elemTypeName, sizes] = arrayTypeAndSize(alias);

      if (isStructType(elemTypeName)) {
        alias = getStructNameByType(elemTypeName);
      } else {
        alias = elemTypeName;
      }

      arrayType = sizes.map(size => `[${size}]`).join('');
    }


    if (BasicScryptType[alias]) {
      return `${alias}${arrayType}`;
    }

    if (resolvedTypes[alias]) {
      return `${resolvedTypes[alias]}${arrayType}`;
    }

    return `struct ${alias} {}${arrayType}`;
  };
}

import { bin2num, bsv, num2bin } from './utils';

const Script = bsv.Script;
const Opcode = bsv.Opcode;
const BN = bsv.crypto.BN;
// number of bytes to denote state length after serialization, exclusing varint prefix
export const STATE_LEN_2BYTES = 2;
export const STATE_LEN_4BYTES = 4;

function serializeBool(flag: boolean): string {
  return flag ? 'OP_TRUE' : 'OP_FALSE';
}

function serializeInt(n: number | bigint): string {
  const num = new BN(n);
  if (num.eqn(0)) {
    return '00';
  }
  return num.toSM({ endian: 'little' }).toString('hex');
}

function serializeString(str: string) {
  if (str === '') {
    return '00';
  }
  const buf = Buffer.from(str, 'utf8');
  return buf.toString('hex');
}

// TODO: validate
function serializeBytes(hexStr: string): string {
  return hexStr;
}

export type State = Record<string, boolean | number | bigint | string>;
export type StateArray = Array<boolean | number | bigint | string>;

function serializeWithSchema(state: State | StateArray, key: string | number, schema: State | StateArray = undefined) {
  const type = schema[key];
  if (type === 'boolean') {
    return serializeBool(state[key]);
  } else if (type === 'number') {
    return serializeInt(state[key]);
  } else if (type === 'bigint') {
    return serializeInt(state[key]);
  } else if (type === 'string') {
    return serializeString(state[key]);
  } else {
    return serializeBytes(state[key]);
  }
}

function serialize(x: boolean | number | bigint | string) {
  if (typeof x === 'boolean') {
    return serializeBool(x);
  }
  if (typeof x === 'number') {
    return serializeInt(x);
  }
  if (typeof x === 'bigint') {
    return serializeInt(x);
  } else {
    return serializeBytes(x);
  }
}

// serialize contract state into Script ASM
export function serializeState(state: State | StateArray, stateBytes: number = STATE_LEN_2BYTES, schema: State | StateArray = undefined): string {
  const asms = [];

  const keys = Object.keys(state);
  for (const key of keys) {
    if (schema) {
      const str = serializeWithSchema(state, key, schema);
      asms.push(str);
    } else {
      const str = serialize(state[key]);
      asms.push(str);
    }
  }

  const script = Script.fromASM(asms.join(' '));
  const scriptHex = script.toHex();
  const stateLen = scriptHex.length / 2;

  // use fixed size to denote state len
  const len = num2bin(stateLen, stateBytes);
  return script.toASM() + ' ' + len;
}

class OpState {
  public op: any;

  constructor(op) {
    this.op = op;
  }

  toNumber(): number | string | bigint {

    if (this.op.opcodenum === Opcode.OP_1) {
      return Number(1);
    } else if (this.op.opcodenum === Opcode.OP_0) {
      return Number(0);
    } else if (this.op.opcodenum === Opcode.OP_1NEGATE) {
      return Number(-1);
    } else if (this.op.opcodenum >= Opcode.OP_2 && this.op.opcodenum <= Opcode.OP_16) {
      return Number(this.op.opcodenum - Opcode.OP_2 + 2);
    } else {
      if (!this.op.buf) throw new Error('state does not have a number representation');
      return Number(bin2num(this.op.buf));
    }

  }

  toBigInt(): bigint {
    if (this.op.opcodenum === Opcode.OP_1) {
      return BigInt(1);
    } else if (this.op.opcodenum === Opcode.OP_0) {
      return BigInt(0);
    } else if (this.op.opcodenum === Opcode.OP_1NEGATE) {
      return BigInt(-1);
    } else if (this.op.opcodenum >= Opcode.OP_2 && this.op.opcodenum <= Opcode.OP_16) {
      return BigInt(this.op.opcodenum - Opcode.OP_2 + 2);
    } else {
      if (!this.op.buf) throw new Error('state does not have a number representation');
      return bin2num(this.op.buf) as bigint;
    }
  }

  toBoolean(): boolean {
    return this.toNumber() !== Number(0);
  }

  toHex(): string {
    if (!this.op.buf) throw new Error('state does not have a hexadecimal representation');
    return this.op.buf.toString('hex');
  }

  toString(arg = 'utf8') {
    if (!this.op.buf) { throw new Error('state does not have a string representation'); }
    if (this.op.buf[0] === 0) {
      return '';
    }
    return this.op.buf.toString(arg);
  }
}

export type OpStateArray = Array<OpState>

// deserialize Script or Script Hex or Script ASM Code to contract state array and object
export function deserializeState(s: string | bsv.Script, schema: State | StateArray = undefined): OpStateArray | State | StateArray {
  let script: bsv.Script;
  try {
    script = new Script(s);
  } catch (e) {
    script = Script.fromASM(s);
  }
  const chunks = script.chunks;
  const states = [];
  const pos = chunks.length;
  //the last opcode is length of stats, skip
  for (let i = pos - 2; i >= 0; i--) {
    const opcodenum = chunks[i].opcodenum;
    if (opcodenum === Opcode.OP_RETURN) {
      break;
    } else {
      states.unshift(new OpState(chunks[i]));
    }
  }

  //deserialize to an array
  if (!schema) {
    return states;
  }

  //deserialize to an object
  let ret: State | StateArray;
  if (Array.isArray(schema)) {
    ret = [];
  } else {
    ret = {};
  }
  const keys = Object.keys(schema);
  for (let i = 0; i < states.length; i++) {
    const key = keys[i];
    if (!key) {
      break;
    }
    const val = schema[key];
    if (val === 'boolean' || typeof val === 'boolean') {
      ret[key] = states[i].toBoolean();
    } else if (val === 'number' || typeof val === 'number') {
      ret[key] = states[i].toNumber();
    } else if (val === 'bigint' || typeof val === 'bigint') {
      if (typeof BigInt === 'function') {
        ret[key] = states[i].toBigInt();
      } else {
        ret[key] = states[i].toNumber();
      }
    } else if (val === 'string') {
      ret[key] = states[i].toString();
    } else {
      ret[key] = states[i].toHex();
    }
  }

  return ret;
}
